// App.js

import React, { useRef, useEffect } from 'react';
import './App.css';
import Sidebar from './components/Sidebar.jsx'; // Assuming Sidebar component is in components folder
import logo from './logoo.svg';

function App() {
  const homeRef = useRef(null);
  const aboutRef = useRef(null);
  const contactRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      const scrollOffsets = [
        homeRef.current.offsetTop,
        aboutRef.current.offsetTop,
        contactRef.current.offsetTop
      ];

      const currentScroll = window.scrollY + window.innerHeight / 2;
      let activeSection = 0;

      for (let i = 0; i < scrollOffsets.length; i++) {
        if (currentScroll >= scrollOffsets[i]) {
          activeSection = i;
        }
      }

      window.scrollTo({
        top: scrollOffsets[activeSection],
        behavior: 'instant'
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <Sidebar
        homeRef={homeRef}
        aboutRef={aboutRef}
        contactRef={contactRef}
      />
      <div className="content">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <section ref={homeRef} className="section">
            <h1>Home Section</h1>
            <p>Hier k√∂nnte unsere Website sein.</p>
          </section>
          <section ref={aboutRef} className="section">
            <h1>About Section</h1>
            <p>Learn React</p>
          </section>
          <section ref={contactRef} className="section">
            <h1>Contact Section</h1>
            <p>Contact us here.</p>
          </section>
        </header>
      </div>
    </div>
  );
}

export default App;
