// App.js

import React, { useRef } from 'react';
import './App.css';
import Sidebar from './components/Sidebar.jsx'; // Assuming Sidebar component is in components folder
import logo from './logoo.svg';

function App() {
  const homeRef = useRef(null);
  const aboutRef = useRef(null);
  const contactRef = useRef(null);

  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  };

  return (
    <div className="App">
      <Sidebar
        homeRef={homeRef}
        aboutRef={aboutRef}
        contactRef={contactRef}
        scrollToSection={scrollToSection}
      />
      <div className="content">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <section ref={homeRef} className="section">
            <h1>Home Section</h1>
            <p>Hier k√∂nnte unsere Website sein.</p>
          </section>
          <section ref={aboutRef} className="section">
            <h1>About Section</h1>
            <p>Learn React</p>
          </section>
          <section ref={contactRef} className="section">
            <h1>Contact Section</h1>
            <p>Contact us here.</p>
          </section>
          <button onClick={scrollToTop} className="scroll-btn">Scroll to Top</button>
        </header>
      </div>
    </div>
  );
}

export default App;
